    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {                                         
        try {
            String username = getTxtUsername().getText().trim();
            String password = String.copyValueOf(getTxtPassword().getPassword());
            validateForm(username, password);

            user = Communication.getInstance().login(username, password);

            JOptionPane.showMessageDialog(this, "Uspešno ste se prijavili. Dobrodošli, " + user.getUsername());
            (new FrmMain(user)).setVisible(true);
            this.dispose();

        } catch (Exception ex) {
            JOptionPane.showMessageDialog(this, ex.getMessage());
            
        }
    }                                        

