    private void validateForm(String username, String password) throws Exception {
        String errorMessage = "";
        if (username.isEmpty()) {
            errorMessage += "Korisničko ime mora biti popunjeno!\n";
        }
        if (password.isEmpty()) {
            errorMessage += "Šifra mora biti popunjena!\n";
        }
        if (!errorMessage.isEmpty()) {
            throw new Exception(errorMessage);
        }
    }

    
